#!/usr/bin/env bash
#curls from spg-iso to spg-psn-ext (not possible in psn environment
alias .curl.mtls.spg.iso.sandpit.spgw-ext-psn='curl --key /usr/local/etc/certs/hmpps-delius-sandpit/spg-iso-tls/spgw-ext.sandpit.probation.service.justice.gov.uk.key --cert /usr/local/etc/certs/hmpps-delius-sandpit/spg-iso-tls/spgw-ext.sandpit.probation.service.justice.gov.uk.public-certificate.pem  --cacert /usr/local/etc/certs/hmpps-delius-sandpit/private_ca/privateca.sandpit.probation.service.justice.gov.uk.public-certificate.pem https://spgw-ext-psn.sandpit.probation.service.justice.gov.uk:9001/cxf/InboundSPGProxy/1.2?wsdl -v'

#curls from spg-iso to spg-psn-int (not possible in psn environment
alias .curl.mtls.spg.iso.sandpit.spgw-int-psn='curl --key /usr/local/etc/certs/hmpps-delius-sandpit/spg-iso-tls/spgw-ext.sandpit.probation.service.justice.gov.uk.key --cert /usr/local/etc/certs/hmpps-delius-sandpit/spg-iso-tls/spgw-ext.sandpit.probation.service.justice.gov.uk.public-certificate.pem  --cacert /usr/local/etc/certs/hmpps-delius-sandpit/private_ca/privateca.sandpit.probation.service.justice.gov.uk.public-certificate.pem https://spgw-int-psn.sandpit.probation.service.justice.gov.uk:9001/cxf/InboundSPGProxy/1.2?wsdl -v'
